  return (
    <div className="App">
      <h1>ðŸ§  AI News Summarizer & Sentiment Analyzer</h1>
      <NewsForm setResult={setResult} setLoading={setLoading} />
      <ResultDisplay result={result} loading={loading} />
    </div>
  );
}

export default App;
