import React, { useState } from 'react';
import axios from 'axios';

function NewsForm({ setResult }) {
  const [inputType, setInputType] = useState('text');
  const [text, setText] = useState('');
  const [url, setUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = inputType === 'text' ? { text } : { url };
    try {
      const res = await axios.post('http://127.0.0.1:5000/analyze', payload);
      setResult(res.data);
    } catch (error) {
      alert('Something went wrong!');
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="news-form">
      <div className="radio-group">
        <label className="radio-label">
          <input
            type="radio"
            value="text"
            checked={inputType === 'text'}
            onChange={() => setInputType('text')}
            className="radio-input"
          />
          Text
        </label>
        <label className="radio-label">
          <input
            type="radio"
            value="url"
            checked={inputType === 'url'}
            onChange={() => setInputType('url')}
            className="radio-input"
          />
          URL
        </label>
      </div>

      <div className="input-area">
        {inputType === 'text' ? (
          <textarea
            rows="10"
            placeholder="Paste news article text..."
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="input-textarea"
            required
          />
        ) : (
          <input
            type="url"
            placeholder="Paste article URL..."
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            className="input-url"
            required
          />
        )}
      </div>

      <button type="submit" className="submit-btn">ðŸ§  Analyze</button>
    </form>
  );
}

export default NewsForm;
